package practice;

public class forloop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (char c1='A';c1<='Z';c1++) {
			System.out.print(c1 +" ");
		}
		System.out.println("/n **************vvv****************");
		for(char c1='Z';c1>='A';c1--) {
			System.out.print(c1 +" ");
		}

	}

}
