package practice;

public class switchcase2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int A=3;
		switch(A) {
		case 1:
			System.out.println("case 1 yes");
			break;
		case 2:
			System.out.println("case 2 yes");
			break;
		case 3:
			System.out.println("case 3 yes");
			break;
		case 4:
			System.out.println("case 4 yes");
			break;
			default:
				System.out.println("none of this");
				break;
		}
	}
}